"use client"

import { useState } from "react"
import { X, ChevronRight, ChevronDown, FileText, Download } from "lucide-react"

interface OrderItem {
  id: string
  name: string
  description: string
  quantity: number
  unitPrice: number
  totalPrice: number
  image?: string
  specifications?: string[]
}

interface OrderDetailsTableProps {
  items: OrderItem[]
  currency?: string
}

export default function OrderDetailsTable({ items, currency = "$" }: OrderDetailsTableProps) {
  const [expandedItems, setExpandedItems] = useState<Record<string, boolean>>({})
  const [selectedImage, setSelectedImage] = useState<string | null>(null)

  const toggleItem = (id: string) => {
    setExpandedItems((prev) => ({
      ...prev,
      [id]: !prev[id],
    }))
  }

  const formatCurrency = (amount: number) => {
    return `${currency}${amount.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`
  }

  const totalAmount = items.reduce((sum, item) => sum + item.totalPrice, 0)

  return (
    <div className="bg-white rounded-xl shadow-lg overflow-hidden">
      <div className="bg-gradient-to-r from-blue-500 to-blue-600 text-white p-4">
        <div className="flex items-center justify-between">
          <h2 className="text-xl font-bold">Order Details</h2>
          <span className="bg-white/20 text-white px-3 py-1 rounded-full text-sm font-semibold">
            {items.length} {items.length === 1 ? "Item" : "Items"}
          </span>
        </div>
      </div>

      <div className="overflow-x-auto">
        <table className="w-full min-w-[800px]">
          <thead>
            <tr className="bg-gray-50 border-b border-gray-200">
              <th className="py-3 px-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"></th>
              <th className="py-3 px-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Item</th>
              <th className="py-3 px-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Qty</th>
              <th className="py-3 px-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Unit Price
              </th>
              <th className="py-3 px-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Total</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {items.map((item) => (
              <>
                <tr key={item.id} className="hover:bg-gray-50 transition-colors">
                  <td className="py-4 px-4">
                    <button
                      onClick={() => toggleItem(item.id)}
                      className="p-1 hover:bg-gray-200 rounded-full transition-colors"
                    >
                      {expandedItems[item.id] ? (
                        <ChevronDown className="w-4 h-4 text-gray-500" />
                      ) : (
                        <ChevronRight className="w-4 h-4 text-gray-500" />
                      )}
                    </button>
                  </td>
                  <td className="py-4 px-4">
                    <div className="flex items-center">
                      {item.image && (
                        <div
                          className="w-12 h-12 bg-gray-100 rounded-md mr-3 flex-shrink-0 cursor-pointer overflow-hidden"
                          onClick={() => {
                            if (item.image){
                            setSelectedImage(item.image)
                          }else {
                            setSelectedImage("data:image/png;base64,")
                          }
                        }}
                        >
                          <img
                            src={item.image || "/placeholder.svg"}
                            alt={item.name}
                            className="w-full h-full object-cover"
                            onError={(e) => {
                              ;(e.target as HTMLImageElement).src = "data:image/png;base64,"
                            }}
                          />
                        </div>
                      )}
                      <div>
                        <div className="font-medium text-gray-900">{item.name}</div>
                        <div className="text-sm text-gray-500">{item.description}</div>
                      </div>
                    </div>
                  </td>
                  <td className="py-4 px-4 text-gray-900">{item.quantity}</td>
                  <td className="py-4 px-4 text-gray-900">{formatCurrency(item.unitPrice)}</td>
                  <td className="py-4 px-4 font-medium text-gray-900">{formatCurrency(item.totalPrice)}</td>
                </tr>
                {expandedItems[item.id] && (
                  <tr className="bg-gray-50">
                    <td colSpan={5} className="py-3 px-4">
                      <div className="pl-8 pr-4 py-2">
                        <h4 className="text-sm font-medium text-gray-700 mb-2">Specifications:</h4>
                        {item.specifications && item.specifications.length > 0 ? (
                          <ul className="list-disc pl-5 text-sm text-gray-600 space-y-1">
                            {item.specifications.map((spec, index) => (
                              <li key={index}>{spec}</li>
                            ))}
                          </ul>
                        ) : (
                          <p className="text-sm text-gray-500 italic">No specifications available</p>
                        )}
                        <div className="flex gap-3 mt-3">
                          <button className="px-3 py-1 text-xs bg-blue-50 text-blue-600 rounded flex items-center gap-1 hover:bg-blue-100 transition-colors">
                            <FileText className="w-3 h-3" />
                            Spec Sheet
                          </button>
                          <button className="px-3 py-1 text-xs bg-green-50 text-green-600 rounded flex items-center gap-1 hover:bg-green-100 transition-colors">
                            <Download className="w-3 h-3" />
                            Manual
                          </button>
                        </div>
                      </div>
                    </td>
                  </tr>
                )}
              </>
            ))}
          </tbody>
          <tfoot>
            <tr className="bg-gray-50 border-t border-gray-200">
              <td colSpan={4} className="py-3 px-4 text-right font-medium text-gray-700">
                Total:
              </td>
              <td className="py-3 px-4 font-bold text-blue-600">{formatCurrency(totalAmount)}</td>
            </tr>
          </tfoot>
        </table>
      </div>

      {/* Image Modal */}
      {selectedImage && (
        <div className="fixed inset-0 bg-black/70 flex items-center justify-center z-50 p-4">
          <div className="relative max-w-4xl w-full bg-white rounded-lg overflow-hidden">
            <div className="p-4 border-b border-gray-200 flex justify-between items-center">
              <h3 className="font-medium">Image Preview</h3>
              <button
                onClick={() => setSelectedImage(null)}
                className="p-1 hover:bg-gray-100 rounded-full transition-colors"
              >
                <X className="w-5 h-5" />
              </button>
            </div>
            <div className="p-4 flex items-center justify-center">
              <img
                src={selectedImage || "/placeholder.svg"}
                alt="Preview"
                className="max-h-[70vh] max-w-full object-contain"
              />
            </div>
          </div>
        </div>
      )}
    </div>
  )
}
